package corejava;

public class implicitconversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i = 200;
		
		//automatic type conversion
	    double d = i;
		
		//automatic type conversion
		
		System.out.println("Int value: "+i);
		System.out.println("double value: "+d);
		
	}

}
